note_models.css:
.card {
  font-family: arial;
  font-size: 20px;
  text-align: center;
  color: black;
  background-color: white;
}

.cloze {
  font-weight: bold;
  color: blue;
}

.cloze-inactive {
  color: darkslateblue;
}

.nightMode .cloze {
  color: lightblue;
}

ul,
ol {
  display: block;
  text-align: left;
  list-style: inside;
}

table, th, td {
  border: 1px solid black;
  border-collapse: collapse;
}

td[colspan]:not([colspan="1"]) {
    text-align: center;
}

code {
  display: inline-block;
  white-space: pre;
  text-align: left;
  tab-size: 3;
  background: rgba(7,7,7,0.2);
}
